import axios from "axios/index";

import {
    FETCH_ALL_USERS_PUBLIC_DATA
} from '../ActionType';

async function UsersPublicData(token, userId, dispatch) {
    axios.get(process.env.REACT_APP_API_URL + "/users/publicData/" + userId, {
        headers: {
            "Authorization": `Bearer ${token}`
        }
    })
    .then(results => {
        dispatch({
            type: FETCH_ALL_USERS_PUBLIC_DATA,
            payload: results.data
        })
    })
    .catch(err => {
        dispatch({
            type: FETCH_ALL_USERS_PUBLIC_DATA,
            payload: false
        })
    })
}

export function fetchAllUsersPublicData() {
    const token = localStorage.getItem("token");
    const userId = localStorage.getItem("userId");

    return async function (dispatch) {
        await UsersPublicData(token, userId, dispatch);
    }
}
