import React from "react";
import Header from '../../common/Header';
import Sidebar from '../../common/SideBar';
import Footer from '../../common/Footer';
import SearchBar from './SearchBar';
import ResultUser from './ResultUser';
import { connect } from "react-redux";
import { push } from "react-router-redux";
import { fetchAllUsersPublicData } from '../../../store/actions/UsersActions/fetchAllUsersPublicDataAction';
import io from 'socket.io-client';

class Home extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            users: []
        }
    }

    async UNSAFE_componentWillMount() {
        await this.props.onfetchAllUsersPublicData([]);
    }


    initUsers(users) {
        this.setState({
            users
        });
    }

    UNSAFE_componentWillReceiveProps(nextProps) {
        if (nextProps.fetchAllUsersPublicData !== undefined && nextProps.fetchAllUsersPublicData.code === 200)
            this.initUsers(nextProps.fetchAllUsersPublicData.data);
    }

    handleChangeFilterCat(event) {
    }

    async activePagination(event) {
    }


    initPagination(active) {
        let allPagination = [];
        let totalPage = 0;

        if (this.state.selectedCategory !== "")
            totalPage = this.props.totalPageFilteredMembers
        else
            totalPage = this.props.totalPage;

        for (var i = 0; i < totalPage; i++) {
            if (i === this.state.currentPagination)
                allPagination.push(<li onClick={this.activePagination} key={i} className="active"><span style={{ 'cursor': 'pointer' }} id={i}>{i + 1}</span></li>);
            else
                allPagination.push(<li onClick={this.activePagination} key={i}><span style={{ 'cursor': 'pointer' }} id={i}>{i + 1}</span></li>);
        }
        i = 0;
        return allPagination;
    }


    render() {
        let filteredUsers = this.state.users;
        return (
            <div>
                <Header />
                <Sidebar />
                <div>
                    <div className="content-wrapper">
                        <section className="content">

                            <section className="content">
                                <br />
                                <SearchBar />
                                <ResultUser users={filteredUsers} />

                            </section>


                        </section>
                    </div>
                </div>
                <Footer />
            </div>

        );
    }
}

const state = (state, ownProps = {}) => {
    return {
        fetchAllUsersPublicData: state.fetchAllUsersPublicData.fetchAllUsersPublicData,
        location: state.location,
    }
}

const mapDispatchToProps = (dispatch, ownProps) => {
    return {
        navigateTo: (location) => {
            dispatch(push(location));
        },
        onfetchAllUsersPublicData: (filter) => dispatch(fetchAllUsersPublicData(filter))
    }
};

export default connect(state, mapDispatchToProps)(Home);
